mapper.select:
 
		SELECT A.* FROM ( SELECT B.*, ROWNUM RN FROM ( 
		SELECT SEQ            
			   ,NAME         
			   ,CONTENT      
			   ,BOARD_SEQ
			   ,REGDATE
		  FROM COMMENTS  
		 WHERE BOARD_SEQ = #{boardSeq}
		  
		   
		  	and seq > #{seq}
		  
		  
		 ORDER BY SEQ DESC 
		 ) B ) A
		   
		 WHERE RN <= #{pageUnit}

mapper.insert:

	select nvl(max(seq),0)+1 from comments

INSERT INTO COMMENTS(
		  SEQ  
		, NAME
		, CONTENT
		, BOARD_SEQ
		, REGDATE
		) 
	VALUES(
		  #{seq}
		, #{name}
		, #{content}
		, #{boardSeq}
		, SYSDATE
		
		)

mapper.update:
	update  COMMENTS SET 
			name = #{name}
		    ,content = #{content} 
     where  seq = #{seq}
